import { Avatar } from "@mui/material";
import React, { useState } from "react";
import "./Post.css";
import { RxDotsHorizontal } from "react-icons/rx";
import pic from "../../../images/DSC09653.jpeg";
import love from "../../../images/love.svg";
import comment from "../../../images/comment.svg";
import share from "../../../images/share.svg";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import { PostAddRounded } from "@mui/icons-material";

export default function Post() {
  const [isFocused, setIsFocused] = useState(false);
  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  return (
    <div className="post-container">
      {/*Header */}
      <div className="post-header">
        <Avatar className="post-image" src="" />
        <div className="post-username">Username</div>
        <div className="post-dot-option">
          {" "}
          <RxDotsHorizontal style={{ fontSize: "24px" }} />{" "}
        </div>
      </div>

      {/*Image */}
      <div>
        <img src={pic} width="615px" />
      </div>

      {/*Analytics */}
      <div>
        <div>
          <img src={love} className="post_reactimage" />
          <img src={comment} className="post_reactimage" />
          <img src={share} className="post_reactimage" />
        </div>

        <div style={{ fontWeight: "bold", marginLeft: "7px" }}>100 likes</div>
      </div>

      {/*Comment Section */}
      <div>
        <div className="post_comment">Hello</div>
        <div className="post_comment">Hello</div>
        <div className="post_comment">Hello</div>
        <div className="post_comment">Hello</div>
        <input
          onFocus={handleFocus}
          onBlur={handleBlur}
          text="text"
          className="post_commentbox"
          placeholder="Add a comment..."
        />
        {isFocused && <span >Post</span>}
      </div>
    </div>
  );
}
